package main

import (
	"flag"
	"fmt"
	"log"
	"net/http"

	"github.com/a-liut/gio-api-gateway-ms/src/api"
)

func main() {
	var port int
	flag.IntVar(&port, "port", 8080, "port to be used")

	flag.Parse()

	log.Printf("Server started on port %d", port)

	router := api.NewRouter()

	p := fmt.Sprintf(":%d", port)

	log.Fatal(http.ListenAndServe(p, router))
}
